<?php\n// test_extension.php - тестовый скрипт для проверки работы PHP расширения\n\n// Проверяем, что расширение загружено\nif (!extension_loaded('cryphptor')) {\n    die(\"Расширение cryphptor не загружено\\n\");\n}\n\necho \"Расширение cryphptor загружено успешно\\n\";\n\n// Пример использования функции для создания пути к зашифрованному файлу\n$encrypted_file_path = \"/path/to/encrypted/file.php\";  // Путь к зашифрованному файлу\n$cryphptor_path = cryphptor_open_encrypted($encrypted_file_path);\n\necho \"Преобразованный путь: $cryphptor_path\\n\";\n\n// Для проверки работы расширения можно попытаться получить содержимое зашифрованного файла\n// Это будет автоматически расшифровано через stream wrapper\n/*\n$content = file_get_contents($cryphptor_path);\necho \"Содержимое файла после расшифровки:\\n\";\necho $content;\n*/\n?>